section.screen:
#!/bin/bash TITLE="Client"
#USER=$(whiptail --inputbox "User:" 8 60 --title "$TITLE" 3>&1 1>&2 2>&3)
#PASSWD=$(whiptail --title "$TITLE" --passwordbox "Passsword:" 8 60 3>&1 1>&2 2>&3)
COMMAND=$(whiptail --title "$TITLE" --menu "Administrator Tools" 22 50 10 \ "ping" "ping" \ "tracepath" "tracepath" \ "top" "top" \ "free" "free" \ "ps" "ps" \ "netstat" "netstat" \ "lsof" "lsof" \ "iftop" "iftop" \ "log" "log" \ 3>&1 1>&2 2>&3)
function option(){ OPTION=$(whiptail --inputbox "COMMAND-LINE Options: " 8 60 --title "$TITLE" 3>&1 1>&2 2>&3) }
function weblog(){ LOG=$(whiptail --title "$TITLE" --menu "Logs" 22 50 8 \ "/var/log/messages" "message" \ "/var/log/syslog" "syslog" \ "/var/log/nginx/access.log" "access.log" \ "/var/log/nginx/error.log" "error.log" \ 3>&1 1>&2 2>&3)
}
case $COMMAND in ping) option $COMMAND $OPTION ;; tracepath) option $COMMAND $OPTION ;; free) $COMMAND -m read ;; top|iftop) $COMMAND ;; log) weblog tail -f $LOG ;; ps|lsof) option $COMMAND $OPTION read ;; netstat) option $COMMAND $OPTION read ;; *) exit $? esac
